import { useEffect, useState } from "react";
import RouterInfo from "../components/routes/RouterInfo";

const useHasPath = (path: string): boolean => {
  console.log(path.indexOf("/"));
  if (path.includes("/")) {
    return true;
  }

  return RouterInfo[path].hasNav;
};

export default useHasPath;
