import { Route, Routes, useLocation } from "react-router-dom";
import RouterInfo, { RouterItem } from "./RouterInfo";
import PrivateRoute from "./PrivateRouter";
import React, { useEffect } from "react";

interface RoutesObjectProps {
  hasNav: boolean;
  setHasNav: React.Dispatch<React.SetStateAction<boolean>>;
}

const RoutesObject = ({
  hasNav,
  setHasNav,
}: RoutesObjectProps): JSX.Element => {
  const handleNavOnUpdate = (isWithNav: boolean) => {
    setHasNav(isWithNav);
  };

  const { pathname } = useLocation();

  return (
    <Routes>
      {Object.entries(RouterInfo).map(([key, item]: [string, RouterItem]) => {
        return (
          <Route
            key={item.path}
            path={item.path}
            element={
              <>
                item.isLoggedIn ? (<PrivateRoute>{item.element}</PrivateRoute>)
                : ({item.element})
              </>
            }
          />
        );
      })}
    </Routes>
  );
};

export default RoutesObject;
