/** @jsxImportSource @emotion/react */
import React from "react";
import { css } from "@emotion/react";
import { themeId } from "styles/emotion";
import theme from "styles/theme";
// import { buttonStyles } from "../../../styles/components/Button";

interface ButtonProps {
  className?: string;
  children: React.ReactNode;
}

const BasicButton: React.FC<ButtonProps> = ({
  className,
  children,
  ...rest
}) => {
  const buttonStyles = css`
    font-size: 20px;
    border: 0;
    background-color: ${theme[themeId].backgroundColor};
    display: flex;
    justify-content: center;
    align-items: center;
    color: #fff;
    cursor: pointer;
  `;

  return (
    <>
      <button {...rest} css={buttonStyles} className={`${className || ""}`}>
        {children}
      </button>
    </>
  );
};

export default BasicButton;
