import { css } from '@emotion/react';

export const buttonStyles = css`
  font-size: 16px;
  border: 0;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 10px 14px;
  background-color: royalblue;
  color: #fff;
`;
