/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { GlobalStyles } from "./styles/GlobalStyles";

import SideBar from "./components/common/SideBar";
import RoutesObject from "./components/routes";
import { useLocation } from "react-router-dom";
import RouterInfo from "./components/routes/RouterInfo";

const mainStyles = css`
  display: flex;
  height: 100vh;
`;

const contentStyles = css`
  flex: auto;
  height: 100%;
  padding: 20px;
`;

function App() {
  const pathname = useLocation().pathname;

  return (
    <>
      <GlobalStyles />

      <main css={mainStyles}>
        {<>{console.log(RouterInfo[pathname].hasNav)}</>}
        {RouterInfo[pathname]?.hasNav && <SideBar />}

        <div css={contentStyles}>{RoutesObject()}</div>
      </main>
    </>
  );
}

export default App;
